import { Button, Flex, Input } from "@chakra-ui/react"
import { useState } from "react";
import { ViewIcon, ViewOffIcon } from '@chakra-ui/icons';


export const CashierLogin = () => {
    const [show, setShow] = useState(false)
    const handleClick = () => setShow(!show)
    return (
        <>
            <Flex justifyContent={"center"}>
                <Input variant={"flushed"} color={"black"} borderBottom={"2px solid"}
                    borderColor={"#D5AD18"} w={"400px"} placeholder="Phone Number" size={"md"} />
            </Flex>
            <Flex justifyContent={"center"}>
                <Input ml={"32px"} w={"400px"} placeholder="Password" size={"md"} type={show ? 'text' : 'password'}  variant={"flushed"} color={"black"} borderBottom={"2px solid"} borderColor={"#D5AD18"} />
                <Button right={"10px"} variant={"unstyled"} size='sm' onClick={handleClick}>
                    {show ? <ViewIcon /> : <ViewOffIcon />}
                </Button>
            </Flex>

        </>
    );
}
